// option java_package = "msg";
// option java_outer_classname = "MjMessage";
package msg.MjMessage;
import "roomMessage.proto";





//麻将
message Mj{
	optional int32 num = 1;//牌号码
	optional int32 type = 2;//牌花色
}
//鹰潭飞宝
message FeiBao{
	repeated int32 baoPaiShaizi = 1;//宝牌色子
	optional Mj dieBaoPai = 2;//跌宝牌
	optional Mj baoPai = 3;//宝牌
}
//癞子
message LaiZi{
	optional Mj dieLaiZiPai = 1;//跌宝牌
	optional Mj laiZiPai = 2;//宝牌
}
//发牌推送
message FaPaiCast{
	optional int32 round = 1;// 第几把
	optional int32 bankerSeetIndex = 2;//庄家位置
	repeated Mj pais = 3;//初始13张牌
	repeated int32 shaizi = 4;
	optional bool baoTing = 5;//能否报听
	optional bool hasBaoTing = 6;//有人报听否？
	optional FeiBao feiBao = 7;//鹰潭飞宝
	optional LaiZi laiZi = 8;//癞子
}
//报听请求
message BaoTingCm{
	optional bool baoTing = 1;//是否报听
}
//报听返回
message BaoTingSm{
	optional int32 code = 1;//1ok
	optional bool baoTing = 2;//是否报听
}
//报听广播
message BaoTingCast{
	optional int32 seetIndex = 1;//谁报听 不报听为0
}
//听
message TingHuPai{
	optional Mj ting = 1;//听什么牌 
	repeated int32 huPaiType = 2;//胡牌类型 1天胡...16平胡 17门清
	optional int32 fanShu = 3;//番数
	optional bool anyPai = 4;//任何牌
}
//打哪个牌听牌
message Ting{
	optional Mj mj = 1;//打什么牌
	repeated TingHuPai tingHuPai = 2;//听
}
//抓牌推送
message ZhuaPaiCast{
	optional Mj mj = 1;
	optional bool ziMo = 2;//是否能自摸
	repeated Mj anGang = 3;//暗杠的牌
	repeated Mj mingGang = 4;//明杠牌
	repeated Ting ting = 5;//打哪个牌听牌
	optional bool bu = 6;//是不是补牌
	optional bool baoTing = 7;//能否报听
	optional int32 mingPaiStatus = 8; //明牌状态   0.未明牌，1.可以明牌 2.点了明牌未打牌  3.已明牌
}
//谁抓牌广播
message SeetZhuaPaiCast{
	optional int32 seetIndex = 1;
	optional bool bu = 2;//是不是补牌
}
//自摸牌请求
message ZiMoPaiCm{
}
//自摸牌返回
message ZiMoPaiSm{
	optional int32 code = 1;//1ok
}
//暗杠牌请求
message AnGangPaiCm{
	optional Mj mj = 1;//暗杠什么牌
}
//暗杠牌返回
message AnGangPaiSm{
	optional int32 code = 1;//1ok
	optional Mj mj = 2;//暗杠什么牌
	optional bool display = 3;//是否显示
}
//暗杠牌广播
message AnGangPaiCast{
	optional int32 seetIndex = 1;//位置
	optional Mj mj = 2;
	optional bool display = 3;//是否显示
}
//明杠牌请求
message MingGangPaiCm{
	optional Mj mj = 1;//明杠什么牌
}
//明杠牌返回
message MingGangPaiSm{
	optional int32 code = 1;//1ok
	optional Mj mj = 2;//明杠什么牌
}
//明杠牌广播
message MingGangPaiCast{
	optional int32 seetIndex = 1;//位置
	optional Mj mj = 2;
	optional bool qiangGang = 3;//是否能抢杠
}
//打牌请求
message DaPaiCm{
	optional Mj mj = 1;
	optional bool shangLuo = 2;//是否选择上落
	optional bool baoTing = 3;//是否选择报听
}
//打牌返回
message DaPaiSm{
	optional int32 code = 1;//1ok
	optional Mj mj = 2;
	optional bool shangLuo = 3;//是否选择上落
	optional bool baoTing = 4;//是否选择报听
}
//打牌广播
message DaPaiCast{
	optional int32 seetIndex = 1;//位置
	optional Mj mj = 2;
	optional bool peng = 3;//是否能碰
	optional bool jieGang = 4;//是否能接杠
	optional bool jiePao = 5;//是否能接炮
	repeated int32 chi = 6;//1吃小边 2吃中间 3吃大边 
	optional bool shangLuo = 7;//是否选择上落
	optional bool baoTing = 8;//是否选择报听
}
//接炮请求
message JiePaoCm{
}
//接炮返回
message JiePaoSm{
	optional int32 code = 1;//1ok
}
//接杠请求
message JieGangCm{
}
//接杠返回
message JieGangSm{
	optional int32 code = 1;//1ok
	optional Mj mj = 2;
	optional int32 daSeetIndex = 3;//谁出的
}
//接杠广播
message JieGangCast{
	optional int32 seetIndex = 1;//位置
	optional Mj mj = 2;
	optional int32 daSeetIndex = 3;//谁出的
}
//碰牌请求
message PengPaiCm{
}
//碰牌返回
message PengPaiSm{
	optional int32 code = 1;//1ok
	optional Mj mj = 2;
	optional int32 daSeetIndex = 3;//谁出的
}
//碰牌广播
message PengPaiCast{
	optional int32 seetIndex = 1;//位置
	optional Mj mj = 2;
	repeated Mj anGang = 3;//暗杠的牌
	repeated Mj mingGang = 4;//明杠牌
	repeated Ting ting = 5;//打哪个牌听牌
	optional int32 daSeetIndex = 6;//谁出的
	optional int32 mingPaiStatus = 7; //明牌状态   0.未明牌，1.可以明牌 2.点了明牌未打牌  3.已明牌
}
//吃牌请求
message ChiPaiCm{
	optional int32 chi = 1;//1吃小边 2吃中间 3吃大边 
}
//吃牌返回
message ChiPaiSm{
	optional int32 code = 1;//1ok
	optional Mj mj = 2;
	optional int32 chi = 3;//1吃小边 2吃中间 3吃大边 
}
//吃牌广播
message ChiPaiCast{
	optional int32 seetIndex = 1;//位置
	optional Mj mj = 2;
	optional int32 chi = 3;//1吃小边 2吃中间 3吃大边 
	repeated Mj anGang = 4;//暗杠的牌
	repeated Mj mingGang = 5;//明杠牌
	repeated Ting ting = 6;//打哪个牌听牌
	optional int32 mingPaiStatus = 7; //明牌状态   0.未明牌，1.可以明牌 2.点了明牌未打牌  3.已明牌
}
//胡牌结果
message HuResult {
	optional int32 seetIndex = 1;//位置
	repeated int32 huPaiType = 2;//胡牌类型 1天胡 2地胡 3报听 4将将胡 5清一色 6一条龙 7碰碰胡 8小七对 9豪华小七对 10双豪华小七对 11三豪华小七对 12海底 13抢杠胡 14杠爆 15一字撬 16平胡 17门清
}
//杠碰吃记录
message TargetMj{
	optional int32 type = 1;//1暗杠 2明杠 3接杠 4碰 5吃
	optional int32 seetIndex = 2;//谁出的
	optional Mj mj = 3;
	optional int32 chi = 4;//1吃小边 2吃中间 3吃大边 
	optional bool display = 5;
}
//局结果
message RoundResult {
	optional int32 seetIndex = 1;//位置
	optional int32 rs = 2;//分数变化
	optional int32 niaoNumber = 3;//中鸟个数
	optional int32 anGang = 4;//暗杠个数
	optional int32 mingGang = 5;//明杠个数
	optional int32 jieGang = 6;//接杠个数
	optional int32 fangPao = 7;//放炮个数
	optional int32 fangGang = 8;//放杠个数
	repeated Mj mj = 9;//手上的牌
	optional int32 fangQiangGang = 10;//放抢杠胡个数
	optional bool yiZiQiaoYouXi = 11;//是否一字撬有喜
	optional int32 coin = 12;
	repeated TargetMj gpc = 13;
	optional int32 baoNum = 14;//宝牌数目
	optional int32 paoFen = 15;//抛分的加扣数
	optional int32 huPaiGang = 16;//胡牌的人几个杠
}
//鸟
message Niao{
	optional int32 seetIndex = 1;//位置
	optional Mj mj = 2;
}
//局结算广播
message RoundResultCast {
	optional Mj mj = 1;//胡的什么牌
	optional int32 getHuPaiType = 2;//1接炮 2自摸 3抢杠
	repeated HuResult hu = 3;//哪几个胡牌
	repeated RoundResult rs = 4;//结果
	optional bool liuju = 5;//是否流局
	repeated Niao niao = 6;//鸟
	optional int32 niaoRen = 7;
	optional bool reconnect = 8;//是否是断线重连发的
	repeated Mj maiMa = 9; //买马 
}
//战绩
message SeetResult{
	optional int32 seetIndex = 1;//哪个位置
	optional int32 coin = 2;//当前积分
	optional int32 ziMo = 3;//
	optional int32 jiePao = 4;//
	optional int32 anGang = 5;//
	optional int32 mingGang = 6;//
	optional int32 fangPao = 7;//
	optional int32 jieGang = 8;//
	optional int32 fangGang = 9;//	
}
//游戏结束
message GameOverCast{
	repeated SeetResult rs = 1;
	repeated int32 seetIndex = 2;//大赢家
	repeated int32 paoShou = 3;//最佳炮手
}
//断线重连请求
message ReconnectCm {
}
//我的本局信息
message MyRoundInfo{
	repeated Mj pais = 3;//13张牌
	repeated int32 shaizi = 4;
	optional bool baoTing = 5;//能否报听
	optional bool hasBaoTing = 6;//有人报听否？
	optional int32 leftPaiNum = 7;//剩余的牌数目
	optional int32 buPaiNum = 8;//补了几张牌
}

//明牌广播
message MingPaiCast{
	optional int32 seetIndex = 1;   //座位号
	repeated Mj pais = 2;// 明牌玩家的所有牌.
	repeated Mj hideMj = 3; //隐藏牌
	repeated Mj tingMj = 4;// 听牌
	repeated Mj kouMj = 5;// 扣的牌

}


//玩家局信息
message UserRoundInfo{
	optional int32 seetIndex = 1;//哪个位置
	optional bool baoTing = 2;//是否报听
	repeated Mj mj = 3;//打的牌
	repeated TargetMj gpc = 4;
	optional int32 num = 5;//多少张牌
	optional MingPaiCast mingPaiCast = 6;// 手牌  明牌玩家的所有牌.  
}
//断线重连返回
message ReconnectSm {
	optional int32 code = 1;//1ok
	repeated RoomMessage.UserInfo users = 2;//玩家列表
	optional RoomMessage.PlayType playType  = 3; //玩法
	optional string roomId = 4;//房间id
	optional string chatRoomId = 5;//
	repeated UserRoundInfo usersRound = 6;
	optional bool start = 7;//游戏是否开始
	optional MyRoundInfo my = 8;//我的局信息
	optional int32 round = 9;// 第几把
	optional int32 bankerSeetIndex = 10;//庄家位置
	optional RoomMessage.JulebuRoom juelebuRoom  = 11; //俱乐部房间
	optional FeiBao feiBao = 12;//鹰潭飞宝
	optional LaiZi laiZi = 13;//癞子
	
}
//报听自动操作广播
message BaoTingAutoCast{
	optional Mj mj = 2;
}
//报听抓牌打牌广播 此消息和DaPaiCast一模一样，注意！
message BaoTingZhuaDaPaiCast{
	optional int32 seetIndex = 1;
	optional Mj mj = 2;
	optional bool peng = 3;//是否能碰
	optional bool jieGang = 4;//是否能接杠
	optional bool jiePao = 5;//是否能接炮
	repeated int32 chi = 6;//1吃小边 2吃中间 3吃大边 
}
//过请求
message OverCm{
}
//过返回
message OverSm{
	optional int32 code = 1;//1ok
}
//抢杠请求
message QiangGangCm{
}
//抢杠返回
message QiangGangSm{
	optional int32 code = 1;//1ok
}
//海底自动操作广播
message HaiDiZhuaPaiCast{
	optional Mj mj = 1;
	optional bool ziMo = 2;
	repeated Mj anGang = 3;//暗杠的牌
	repeated Mj mingGang = 4;//明杠牌
}
//开始抛分广播
message BeginPaoFenCast{
}
//抛分请求
message PaoFenCm{
	optional int32 pao = 1;//0,1,2,3
}
//抛分返回
message PaoFenSm{
	optional int32 code = 1;//1ok
	optional int32 pao = 2;//0,1,2,3
}
//抛分广播
message PaoFenCast{
	optional int32 seetIndex = 1;//座位号
	optional int32 pao = 2;//0,1,2,3
}

// 请求明牌
message MingPaiCm{

}
// 返回明牌
message  MingPaiSm{
	optional int32 code = 1;//1.OK  其他失败
	repeated Ting ting = 2;//打哪个牌听牌
	repeated Mj kouPai = 3;// 明牌玩家的所有牌. 如果值为0.不显示这张牌
}

// 请求扣牌
message MingPaiKouCm{
	optional string kouPaiJson = 1;// 玩家所扣的牌
}

//返回扣牌
message MingPaiKouSm{
	optional int32 code = 1; //返回值，1成功 ，其他失败
	repeated Ting ting = 2;//打哪个牌听牌
}


// 请求取消明牌
message CancelMingPaiCm{

}

// 返回取消明牌
message CancelMingPaiSm{
	optional int32 code = 1;//1.OK  其他失败
	repeated Ting ting = 2;//打哪个牌听牌
}
