syntax = "proto2";

package com.games.douniu;

//rule_type   
// 	1	长沙麻将			
// 	2	长沙258麻将	
// 	3	株洲转转麻将		
// 	4	湘潭转转麻将
// 	5	湘潭258麻将	

//游戏类型结构
message GameType {
	optional int32 		rule_type = 1;			//玩法类型，区分同一个游戏服务器中不同的玩法
	optional RuleDouniu2     rule = 2; 				//游戏规则，具体格式由游戏服务器和客户端议定
	optional int32		players = 3;			//游戏人数
	optional bool       isDating = 4;			//是否是大厅房间

	repeated string 	backup_list = 100; 		//预留
}

//以下为客户端发给服务器的命令和回应

//命令ID：1，心跳包
message CMD_HeartBeat {
}
message RSP_HeartBeat {
}

//命令ID：2，请求登录
message CMD_Login {
	optional uint64 UserID = 1; 	//用户ID
	optional string Password = 2;  //登录大厅时接收的
	optional PlayerDetail UserData = 3; //用户附加信息。具体格式由客户端定义。
}

message RSP_Login {
	optional uint32 Error = 1;  //0表示成功，非0表示服务器拒绝。如果拒绝，则忽略以下全部参数。
	optional uint32 RoomNum = 2; //如果正在游戏中，则表示房号。为0表示不在游戏中。
	optional uint32 SeatID = 3; //如果在房间中，则表示座位号
	optional GameType game_type = 4; //如果在房间中，则表示游戏规则，具体格式由游戏服务器和客户端议定。
	optional string IP = 5;
}

//命令ID：3，请求创建并进入房间
message CMD_CreateRoom {
	optional GameType game_type = 1; 	//游戏类型结构（GameType压成的string）
}
message RSP_CreateRoom {
	optional uint32 RoomNum = 1; //房间号。为0表示创建失败。如果失败，则忽略以下除ErrorInfo之外的全部参数。
	optional uint32 SeatID = 2; //座位号
	optional Room_Error ErrorInfo = 3; //如果创建失败，则表示失败的详细信息，具体格式由游戏服务器和客户端议定。创建成功则忽略该值。
}

message Room_Error
{
	enum Error{
		ERR_OK = 1;
		ERR_NOGOLD = 2;//金币不足
		ERR_NODIAMOND = 3;//钻石不足
		ERR_NOSEAT = 4;//游戏一开始，不能进入了
		ERR_UNKNOWN = 5;//未知错误
		ERR_HASROOM = 6;//已经在别的服务器上有房间
	}
	optional Error err = 1;
}

//命令ID：4，请求进入房间
message CMD_EnterRoom {
	optional uint32 RoomNum = 1;  //房间号
}
message RSP_EnterRoom {
	optional int32 Error = 1; //0表示成功，-1表示房间不存在或已解散，-2表示房间已满，-3表示房间内有相同IP的玩家。如果不成功，则忽略以下全部参数。
	optional uint32 SeatID = 2; //座位号
	optional GameType game_type = 3; //房间的游戏规则，与RSP_Login结构中的Rule一致。
	optional Room_Error ErrorInfo = 4; //如果进入失败，则表示失败的详细信息，具体格式由游戏服务器和客户端议定。进入成功则忽略该值。
}

//命令ID：5，请求进入大厅房间
message CMD_EnterHall {
	optional GameType game_type = 1; 	//游戏类型结构（GameType压成的string）
}
message RSP_EnterHall {
	optional uint32 RoomNum = 1; //房间号。为0表示失败。
	optional uint32 SeatID = 2; //座位号
	optional Room_Error ErrorInfo = 3; //如果进入失败，则表示失败的详细信息，具体格式由游戏服务器和客户端议定。进入成功则忽略该值。
}

//命令ID：6，请求退出房间
message CMD_ExitRoom {
}
message RSP_ExitRoom {
	optional int32 Error = 1; //0表示已退出，非0不允许退出
}

//命令ID：7，聊天信息（包括文字、表情、语音等）
message CMD_Message {
	optional int32 TypeID = 1; //类型ID。具体含义由客户端自行定义。(1是文字,2是表情，3是快捷文字,4是说话语音)

	optional string Message = 2; //内容格式由客户端自行定义。
}

message SoundMessage
{
	optional string sound_url = 1; 
	optional uint32 sound_time = 2; 
}

//该命令没有回应

//命令ID：8，请求房间规则
message CMD_GetRoomRule {
	optional uint32 RoomNum = 1;  //房间号
}

message RSP_GetRoomRule
{
	enum Err {
		ERR_NO = 1;//成功
		ERR_NOROOM = 2;//找不到该房号
	}
	optional GameType game_type = 1; //游戏类型结构（GameType压成的string）
	optional Err error = 2; 
}

//以下为服务器主动发给客户端的信息

//命令ID: 10001，房间内的用户信息
message NTF_RoomUserInfo {  
	optional uint32 SeatID = 1; //座位号
	optional uint64 UserID = 2; //用户ID,为0时表示该玩家已退出
	optional PlayerDetail UserData = 3; //用户附加信息，与CMD_Login中的UserData一致。
}

//命令ID: 10002，聊天信息
message NTF_Message {
	optional uint32 SeatID = 1; //发送人的座位号
	optional int32 TypeID = 2; //与CMD_Message结构中的TypeID一致。
	optional string Message = 3; //与CMD_Message结构中的Message一致。
}

//命令ID: 10003，房间内的用户上线
message NTF_RoomUserOnline {  
	optional uint32 SeatID = 1; //座位号
	optional string IP = 2;
}

//命令ID: 10004，房间内的用户离线
message NTF_RoomUserOffline {  
	optional uint32 SeatID = 1; //座位号
}

//命令ID: 10005，房间已解散
message NTF_RoomDismissed {  
}

//VIP或代开房
//命令ID：9，请求创建VIP房间
message CMD_CreateVIP {
	optional GameType game_type = 1; //游戏规则，具体格式由游戏服务器和客户端议定
}
message RSP_CreateVIP {
	optional int32 id = 1;//序号(每个房间都有一个唯一的序号)(对应VIPRoomInfo中的id)
	optional uint32 roomid = 2; //房间号。为0表示创建失败。如果失败，则忽略以下除ErrorInfo之外的全部参数。
	optional Room_Error error_info = 3; //如果创建失败，则表示失败的详细信息，具体格式由游戏服务器和客户端议定。创建成功则忽略该值。
}
//命令ID：10，请求玩家创建的所有VIP房间信息
message CMD_GetCreatedVIP {
	optional int32 page = 1;
	optional int32 record = 2;//请求类型，1、当前已开房间，2、以前的开房记录
}
message RSP_GetCreatedVIP {
	optional bool more = 1;//是否还有数据
	optional int32 current_pager = 2;//当前页数
	optional int32 pagers_count = 3;//总页数
	repeated VIPRoomInfo room_list = 4;//当前页的VIP房间信息
}
//命令ID：11，玩家解散VIP房间信息
message CMD_DissmissVIP {
	optional int32 id = 1;//序号(每个房间都有一个唯一的序号)(对应VIPRoomInfo中的id)
	optional uint32 roomid = 2;//房间号
}
//命令ID：11，回复玩家解散VIP房间信息
message RSP_DissmissVIP {
	optional int32 id = 1;//序号(每个房间都有一个唯一的序号)(对应VIPRoomInfo中的id)
	optional uint32 roomid = 2;//房间号
	optional int32 err_info = 3;//解散消息,0、正在游戏中，解散失败，1、成功，2、房间不存在,或局数已打完,或房间内的玩家已解散，请查看历史代开记录，其它：未知错误
}

message Players{
	optional uint64 player_id = 1;//玩家ID
	optional string player_name = 2;//玩家姓名
	optional string player_img = 3;//玩家图像
	optional int32 win = 4;//玩家当前总输赢
}
message VIPRoomInfo{
	optional int32 id = 1;//序号(每个房间都有一个唯一的序号)
	optional uint32 roomid = 2;//房间号
	optional string create_time = 3;//创建时间
	optional string rules = 4;//房间规则
	optional bool is_playing = 5;//是否正在游戏
	repeated Players player_list = 6;//房间内玩家信息
	optional int32 operate = 7;// 操作(建房时0,开局1,总结算2,玩家解散3,房主解散4)
	optional string server_ip = 8;//服务器IP
	optional int32 server_port = 9;//服务器port
}

message Niu_Combination
{
	optional int32 level=1;//组合等级或牛数，没牛0<牛1<牛2<牛3<牛4<牛5<牛6<牛7<牛8<牛9<牛牛(10)<四炸(11)<五花牛(12)<五小牛(13,为0时不要判断下面的组合)
	repeated int32 card_3=2;//3张组合
	repeated int32 card_2=3;//2张组合
}

//玩家状态（小结算和总结算全在里面）
message Player_State
{
	repeated int32 pai_list = 1;//牌张
	repeated Niu_Combination niu_list = 2;//可能的牛组合
	optional int32 betscore = 3;//本局下注数,为0时还没有下注(拼三张时为本轮下注总数,便于飞金币动画)
	optional int32 qiangzhuang = 4;//是否抢庄,0,还在想,1抢,2不抢(所有玩家不为0时发牌)
	optional int32 winscore = 5;//本局输赢,正数为赢,负数为输,0为不输不赢(拼三张时为本局下注总数)
	optional int32 score = 6;//当前筹码(玩家剩余筹码总数)
	optional bool ispingniu = 7;//是否已拼牛
	optional bool isowner = 8;//是否房主
	repeated int32 record = 9;//最近的几局详细记录(游戏界面上的历史记录按钮)
	optional bool isready = 10;//准备 0，未准备，1，已准备
	optional int32 seatid = 11;//座位
	optional int32 winscores = 12;//总局输赢
	optional bool need_dingzhuang = 13;//玩家参与定庄
	optional int64 player_id = 14;//玩家ID，发牌状态下刷新座位
	optional int32 niu = 15;//是否有牛
	optional int32 isremider =16; //1提示，2拼牛
	optional bool istouguan=17;//true 托管  false 取消托管
	optional int32 tuoguan_betscore =18; //托管状态下自动下注分数
	optional bool tuoguan_qiangzhuang =19; //托管状态下是否自动抢庄
	optional bool is_mingpai = 20;//拼三张下玩家是否明牌
	optional int32 is_qipai = 21;//拼三张玩家是否弃牌 0、正常打牌 1、弃牌  2、比牌输了
	optional int32 ntimesscore =22; //本局明牌抢庄倍数
	repeated int32 showwinscore = 23;//最近的几局输赢的记录(游戏界面上的历史记录按钮)
	repeated int32 vec_betscore=24;//玩家每轮的下注筹码
}

message Room_Error1
{
	enum Error{
		ERR_OK = 1;
		ERR_NOGOLD = 2;//金币不足
		ERR_NODIAMOND = 3;//钻石不足
		ERR_NOSEAT = 4;//游戏一开始，不能进入了
		ERR_UNKNOWN = 5;//未知错误
	}
	optional Error err = 1;
}

//玩家最近的牛数据
message PlayerNiuRecord
{
	repeated int32 niu_score=1;
}

//单局回放详细玩家数据
message ReplayPlayerInfo
{
	optional uint64 player_id = 1;//玩家ID
	optional string player_name = 2;//玩家姓名
	optional string player_img = 3;//玩家图像
	optional int32 win = 4;//玩家总局输赢
	optional bool isowner = 5;//房主
}

//房间战绩记录明细
message ReplayDetail
{
	optional string time = 1;//对战时间(斗牛没有用到)
	repeated int32 niu = 2;//每个玩家的牛(与玩家一一对应)
	optional int32 seat_zhuang = 3;//庄家方位(1开始)
	optional int32 replay_id = 4;//回放编号
	repeated int64 player_list = 5;//本局玩家列表
}

//战绩数据表(战绩主界面)
message ReplayList
{
	optional int32 room_id = 1;//房间号
	optional string time = 2;//开房时间
	repeated ReplayPlayerInfo player_info = 3;//打牌的人
	repeated ReplayDetail replaydetail = 4;//每局详细 
}


//玩家的信息详情
message PlayerDetail
{
	optional uint64 id = 1;//玩家id
	optional int32 sex = 2;//性别
	optional string headimgurl = 3;//头像链接
	optional string name = 4;//玩家姓名
	//玩家金币，房卡(蓝钻，粉钻等)
	optional int32 money = 5;//金币
	optional int32 diamond_purple = 6;//紫钻(房卡D--对应数据库字段RoomCardD)
	optional int32 diamond_yellow = 7;//黄钻(房卡C--对应数据库字段RoomCardC)
	optional int32 diamond_blue = 8;//蓝钻(房卡B--对应数据库字段RoomCardB)
	optional int32 diamond_pink = 9;//红(粉)钻(房卡A--对应数据库字段RoomCardA)
}
//----------------------------------------------------------------------------------------------------------------
//客户端
//命令ID: 1001，玩家实际动作
//动作类型
message Action {
	//动作类型
	//0								//没有
	//1								//开始游戏(只有房主才能)
	//2								//抢庄(不需要填定其它数据)actdata = 1抢,2不抢(所有玩家不为0时发牌)
	//3								//下注(actdata为玩家下了多少筹码数据)(拼三张跟注,加注,跟所有及策划案中100等按钮客户端按游戏状态及当前玩家自行处理,只需告诉服务端下了多少注就可以了)
	//4								//拼牛(actdata为Niu_Combination用protobuf序列化后的字符串)
	//5								//提示，actdata为空表示按下提示按钮并不显示提示按钮，actdata有数据表示重现提示按钮
	//6								//托管，actdata为空表示托管，actdata非空表示取消托管
	//7								//明牌抢庄下注倍数(actdata为玩家下了多少倍数)
	//8								//弃牌(拼三张)
	//9								//看牌(拼三张)
	//10							//比牌(拼三张actdata为对应比牌方)
	//11							//跟注(拼三张)
	//12							//跟所有(拼三张)
	//13							//播放完动画，actdata有数据：进入下一个游戏状态的字符串
	optional int32 action_type = 1;
	//实际动作方位
	optional int32 seat_id = 2;
	//动作数据
	optional string actdata = 3;
}

//动作类型
message Action2 {
	optional int32 action_type = 1;
	//实际动作方位
	optional int32 seat_id = 2;
	//动作数据
	optional bytes actdata = 3;
}

//命令ID，1301，更换房主
message CMD_ChangeOwner
{
	optional uint64 id = 1;//接收玩家ID
	optional int32 isagree = 2;//1、同意，2、不同意
}

message NTF_ChangeOwner
{
//错误提示
	enum Error{
		ERR_OK = 1;
		ERR_NO = 2;//不允许转让，只有发起移交房主的玩家ID和seat
		ERR_INTRANSFER = 3;//转让中
	}
	optional Error err = 1;
	optional uint64 src_id = 2;//发起移交房主的玩家ID
	optional int32 src_seat=3;
	optional uint64 dst_id = 4;//接收的玩家ID
	optional int32 dst_seat = 5;
	optional int32 error = 6;//1,转让成功，2，不允许转让，3，转让中，4，玩家拒绝
}

//客户端命令ID及服务器广播ID:1101
message CMD_Ready
{
	repeated int32 seat_list = 1;//已准备好的方位,客户端请求时不必填值,服务端广播时会填上已准备好的玩家
}

//命令ID: 1201，解散
message CMD_Dissolve
{
	optional bool is_agree = 1;
}

//回复ID: 1201，解散
message RSP_Dissolve
{
	optional int32 seatid = 1;
	optional int32 is_success = 2;//是否解散成功 -1 解散中， 1 解散成功，0 解散失败
	repeated int32 is_agree = 3;//是否同意解散 0 未选择， 1 发起人 2 同意，3 不同意,-1 没人
}


//请求回放列表ID:301
message CMD_GetReplayList
{
	optional uint64 player_id = 1;//玩家ID
}

//请求单局回放ID:302
message CMD_GetReplay 
{
	optional int32 replay_id = 1;//请求的回放编号
}

//客户端回复再来10局消息ID:1002
message CMD_Again10
{
	optional bool is_agree = 1;//false不同意，ture同意
}
//最后一局推送再来10局消息ID:1002
message NTF_Again10
{
	optional int32 is_again = 1;//1、进行中，显示再来10局提示框，2、成功再来10局，显示多加10局，3、不成功（提议没有通过）
	optional int32 add_jushu = 2;//添加的局数设置
	repeated int32 seat_agree=3;//0表示在选择的方位，1、此方位表示已同意，2、此方位表示不同意,
	optional int32 remain_timer = 4;//剩余时间
	optional int32 all_timer = 5;//倒计时总时间
}
//----------------------------------------------------------------------------------------------------------------
//服务端
//命令ID：1001，游戏状态
message Game_State
{
	enum State
	{
		GAME_STATE_WAITING = 1;//等待玩家进入的界面
		GAME_STATE_ZHUNBEI = 2;//准备(第一局服务端自动准备，不用这一步)
		GAME_STATE_QIANGZHUANG = 3;//普通抢庄
		GAME_STATE_MINGPAIQIANGZHUANG = 4;//明牌抢庄
		GAME_STATE_DINGZHUANG = 5;//定庄
		GAME_STATE_FAPAI = 6;//发牌，并刷新座位
		GAME_STATE_XIAZHU = 7;//下注(下注时服务端不会发出真实牌张)
		GAME_STATE_PINGNIU = 8;//拼牛
		GAME_STATE_RESULT = 9;//结算
		GAME_STATE_ROOMRESULT = 10;//总结算
	}

	optional int32 replay_idx = 1;//回放序号(客户端回放时用,其它时候不要管)
	optional State state = 2;//游戏当前状态
	repeated Player_State playerstate_list = 3;//用户数据
	optional int32 zhuangjia = 4;//庄家方位
	optional int32 now_player = 5;//拼三张时的当前下注玩家
	optional int32 last_player = 6;//拼三张时的最后加注玩家
	optional int32 now_bet = 7;//拼三张时的上一玩家下注的注码
	optional int32 now_round = 8;//拼三张时当前下注轮次
	optional int32 total_bet = 9;//所有玩家总共下注数(锅底)
	optional int32 remain_timer = 10;//倒计时剩余时间

	optional bool dingzhunag_movie = 11;//定庄动画
	optional bool is_vip = 12;//是否为vip房间
	optional int32 err_num = 13;   //开房人数不足返回的消息(0:不处理1:人数不足2：可以开房)


	//结算数据
	optional int32 zjs = 14;//总局数，回放时开始就保存
	optional int32 syjs = 15;//剩余局数，回放时开始就保存
	optional int32 result_type = 16;//结算类型,0、尚未结算(游戏尚未到GAME_STATE_RESULT和GAME_STATE_ROOMRESULT)，1、正常结算，2、流局(玩家解散房间)
}


//----------------------------------------------------------------------------------------------------------------

//回复回放记录列表,ID:301
message RSP_ReplayList
{
	repeated ReplayList replay_list= 1;//回放列表数据
}

//回复单局回放,ID:302
message RSP_Replay
{
	enum Error_Code
	{
		ERROR_NO = 1;//无错误
		ERROR_NOTFIND = 2;//找不到回放
	}
	optional Error_Code error = 1;//错误代码
	repeated ReplayPlayerInfo player_info_list = 2;//玩家信息
	repeated Game_State gamestate_list = 3;//回放记录
	optional string rules = 4;//规则
}

//钻石兑换土豪币,命令ID:401
message CMD_ExchangeGold {
	//钻石数量
	optional int32 diamond_num = 1;
}

//钻石兑换土豪币回应,命令ID:401
message RSP_ExchangeGold {
	//错误代码类型
	enum ErrType {
		ERR_NO = 0;  			//没有错误
		ERR_NOT_ENOUGTH = 1; 	//钻石不够
	}
	//错误代码
	optional ErrType errcode = 1;
	//消耗的钻石数量
	optional int32 diamond_num = 2;
	//获得的土豪币数量
	optional int32 money_num = 3;
}

//APP_ID
//    1010   长沙转转麻将   (大厅)  
//    1011   长沙转转麻将   (自建)
//    1012   长沙转转麻将   (比赛)
//    1020   长沙258麻将   (大厅) 
//    1021   长沙258麻将   (自建)
