option java_package = "msg";
option java_outer_classname = "RoomMessage";
package msg.RoomMessage;

//俱乐部房间
message JulebuRoom{
	optional int32 id = 1;//俱乐部ID
	optional int32 num = 2;//房间编号
}
//同花玩法设置
message ThPlayType{
	optional int32 thRound = 1;//局数 1、4局 2、8局 3、12局
	optional int32 thNum = 2;//几人玩  3人 4人 6人
	optional int32 teamMode = 3;//1、对家同对 2、下家同对 3、不分队
	optional int32 thScoring = 4;//1、加分计算 5同花-9同花按100-500分计算 2、乘分计算 5同花-9同花按100 200 400 ..1600翻倍计算
	optional bool  jiFenBan = 5;//1、显示 2、不显示
	optional bool  isChaiPai = 6;//是否拆牌
	optional int32 whichPair = 7;//几副牌 1.7副 2.8副 3.9副 4.12副 5.15副 6.三人玩法下的4副 7.三人玩法下的5副 
}
//余干麻将玩法设置
message YuGanPlayType{
	optional bool chouZhuang = 1;	//是否有臭庄
	optional bool daiBaoHu = 2;		//是否带宝胡
}
//贵溪卡当麻将玩法设置
message GuiXiKaDangPlayType{
	optional int32 withZhuangMode = 1; //是否玩带庄模式（贵溪卡当）
}
//包麻将玩法设置
message BaoMaJiangPlayType{
	optional int32 daPaiFen = 1; //大牌分计算  0 不计算  1 计算
}
//转转麻将玩法设置
message ZhuanZhuanPlayType{
	optional int32 withZhuangMode = 1; //1、庄家模式 2、闲家模式
	optional int32 yiMaQuanZhongType = 2; //1、一码全中模式 0、非一码全中模式
	optional bool dianPaoHu = 3; //是否打点炮胡
	optional bool youPaiBiHu = 4;//是否打有牌必胡
	optional bool daHongZhongLai = 5; //是否打红中癞
}



//潜江癞晃玩法设置
message QianJiangPlayType{
	optional bool anGang4Times = 1;	//暗杠倍数 true为暗杠4倍，false为暗杠2倍
	optional bool piaoLaiYouJiang = 2;	//是否漂癞有奖
	optional bool shuangDaHu = 3;	//是否双大胡
	optional bool ziMoOnLy = 4;	//是否只许自摸
	optional bool reChong = 5;	//是否热铳
	optional bool tuHaoBiGang = 6; //是否土豪必杠
	optional int32 piaoLaiYouJiangScore = 7;	//漂癞有奖分数
}


//跑的快玩法
message PdkPlayType{
	required int32 pukeCount = 1; // 玩法; 1.十五张牌玩法 2.十六张牌玩法 
	required int32 discardOrder = 2; // 出牌顺序; 1.首局先出 2.每局先出 3.猜单双
	optional bool playBlackThree = 3; // 首轮必出; 首次出牌是否要包含黑桃3
	optional bool fourBringThree = 4; // 是否可以四带三
	optional bool tearBobm = 5; //是否可以拆炸弹
	optional bool userlessBobm = 6; //炸弹是否能打空
	optional bool useRedTen = 7; //是否红桃十漂鸟
	optional bool useRedNine = 8;// 是否红桃九翻倍
	optional bool avoidCheat = 9;//是否防作弊
	optional bool avoidSpring = 10;//是否防强关
	optional bool showPukeCount = 11;//是否显示手牌张数
}


//红中麻将玩法设置
message HongZhongPlayType{
	optional int32 HuType = 1; //1 自模胡,  2  可炮胡
	optional bool qiDui = 2; // 是否 胡七对   
	optional bool qiangGangHu = 3; //是否 抢杠胡 
	optional bool qiangZhiHu = 4;//是否 强制胡牌 
	optional bool paoHuBuZhongMa = 5;//是否 炮胡不中码
	optional int32 zhaMa = 6; //1 不扎码  2 扎2码  3 扎4码  4 扎6码  5 一码全中模式
	optional int32 zhongMaType = 7; // 1 1/5/9 中码   2 庄闲中码
	optional int32 maFen = 8; // 1  1 分  2  2分
	optional bool hongZhongJiePaoHu = 9;// 红中接炮胡
}

//玩家设置玩法
message PlayType{
	optional int32 bankerMode = 1;//局数 1、8局 2、16局
	optional int32 timesLimit = 2;//倍数上限 1、24倍 2、无上限 3、16倍 4、32倍 5、64倍  7、10倍 8、20倍 9、40倍
	optional bool yiTiaoLong = 3;//可选 一条龙
	optional bool menQing = 4;//门清大大胡？
	optional bool jiangJiangHu = 5;//门清将将胡可接炮？
	optional bool baoTing = 6;//报听大胡？
	optional bool diHu = 7;//地胡大胡？
	optional bool yiZiQiao = 8;//一字撬 有喜  没喜
	optional int32 zhaNiao = 9;//扎鸟 1、飞鸟 2、抓鸟
	optional int32 severalNiao = 10;//几个鸟 1、一个鸟 2、两个鸟 3、三个鸟
	optional int32 area = 11;//哪个地方麻将 1沅江 2益阳 3鹰潭飞宝 4鹰潭夹张 9余干麻将 11翻财神 14贵溪卡当
	optional int32 sumN = 12;//总共多少张牌
	optional int32 num = 13;//几人玩 2人 3人 4人
	optional int32 lai = 14;//几个癞子 1,3个癞子 2,4个癞子 3，红中癞子 4,翻财神  5余干麻将癞子
	optional bool  feng = 15;//是否带风
	optional bool shangLuo = 16;//是否上落
	optional bool qiDui = 17;//是否七对
	optional bool qingYiSe  = 18;//是否清一色
	optional bool shiSanLan = 19;//是否十三烂
	optional bool kaQiao = 20;//是否卡撬
	optional int32 paoFen = 21;//0不漂 1漂一次 2每次漂
	optional ThPlayType thPlayType = 22;//同花玩法
	optional bool isQuanPing = 23; //是否全频道.
	optional int32 maiMaLeiXing = 24; // 方买马   1.自摸买马  2.亮倒自摸买马
	optional int32 maiMaShuLiang = 25; // 1.买1马   6.买6马
	optional bool isBuFenLiang = 26; //部分亮牌/少于12张不能明牌
	optional bool isShuKan = 27 ; // 是否数坎
	optional int32 pengPengHuKaiGangTimes = 28 ; //卡五星碰碰胡开杠倍数  4/2倍
	optional bool isShangLou = 29; //上楼
	optional bool isChaJiao = 30;  //查叫
	optional bool isPaoQiaMoBa = 31; //跑恰摸八(卡五星宜城玩法)
	optional PdkPlayType pdkPlayType = 32;//跑得快玩法
	optional YuGanPlayType yuGanPlayType = 33; //余干麻将玩法
	optional uint32 diFen =34; //创建房间底分1 , 2 , 5
	optional uint32 fengDingTaiShu =35; //封顶台数 5 , 6, 7  ,99999999(表示不分顶)
	optional GuiXiKaDangPlayType guiXiKaDangPlayType = 36; //贵溪卡当麻将玩法
	optional BaoMaJiangPlayType baoMaJiangPlayType = 37;// 包麻将玩法设置
	optional ZhuanZhuanPlayType zhuanZhuanPlayType = 38; //转转麻将玩法	
	optional QianJiangPlayType qianJiangPlayType = 39; //潜江癞晃玩法
	optional HongZhongPlayType hongZhongPlayType = 40;//红中麻将玩法
	optional int32 withZhuangMode = 41; //是否玩带庄模式（贵溪卡当）
	optional int32 tuoguan = 42; //是否托管.
	optional bool zhifu = 43;//支付方式  1.房主  2.AA
	optional bool piaohu  = 44;//是否飘胡
	optional bool gangshangpao  = 45;//是否刚上炮
	optional bool diangang  = 46;//是否点杠
	optional bool shisanyao  = 47;//是否十三幺
	
}

//经纬请求
message NsCm{
	optional float j = 1;//经度
	optional float w = 2;//纬度
	optional string ad = 3;//详细地址
	optional string country = 4;//国家
	optional string province = 5;//省份
	optional string city = 6;//城市
	optional string district = 7;//地区
	optional string street = 8;//街道
	optional string houseNumber = 9;//门牌号
}
//经纬返回
message NsSm{
	optional int32 code = 1;//1成功
}
//经纬广播
message NsCast{
	optional NsCm ns = 1;
	optional int32 seetIndex = 2;
}
//玩家信息
message UserInfo{
	optional int64 uid = 1;//玩家名
	optional int32 seetIndex = 2;//座位
	optional int32 gstate = 3;//游戏状态 0未开始 1已准备 2等待他人操作 3选择报听 4自摸、暗杠、明杠、过 5打牌 6接炮、接杠、碰、过 7抢杠、过
	optional int32 userState = 4;//玩家状态 1在线 2断线
	optional int32 imSeatIndex = 5;//语音房间座位号
	optional bool isMaster = 6;//是否房主
	optional string headimg = 7;//头像
	optional string nickname = 8;//玩家昵称
	optional int32 coin = 9;//当前积分
	optional int32 sex = 10;//普通用户性别，1为男性，2为女性
	optional NsCm ns = 11;
	optional string ip = 12;
	optional int32 paoFen = 13;//抛分数
	optional int32 thTeam = 14;//1 绿队 2 红队
}
//切服信息
message SwServer{
	optional int32 sId = 1; //服务器id
	optional string host = 2;//ip
	optional int32 port = 3;//port
	optional string myCode = 4;//code
	optional int32 type = 5;//切服类型 1加入房间 2创建房间 3代理创建房间
}
//房主创建房间预处理请求
message CreateRoomBeforeCm {
	optional PlayType playType = 1;//玩法设置
}
//房主创建房间预处理返回
message CreateRoomBeforeSm {
	optional int32 code = 1;// 2需要切
	optional SwServer sw = 2;
	optional PlayType playType = 3;//玩法设置
}
//代理创建房间预处理请求
message ProxyCreateRoomBeforeCm {
	optional PlayType playType = 1;//玩法设置
}
//代理创建房间预处理返回
message ProxyCreateRoomBeforeSm {
	optional int32 code = 1;// 2需要切
	optional SwServer sw = 2;
	optional PlayType playType = 3;//玩法设置
}
//房主创建房间请求
message CreateRoomCm {
	optional PlayType playType = 1;//玩法设置
}
//房主创建房间返回
message CreateRoomSm {
	optional int32 code = 1; //1成功 2失败
	optional string roomId = 2;//房间ID
	optional string chatRoomId = 3;//聊天室ID
	optional UserInfo info = 4;//
	optional PlayType playType = 5;//玩法
}
//房主加入房间预处理请求
message JoinRoomBeforeCm {
	optional string roomId = 1;
	optional int32 seatIndex = 2;
}
//房主加入房间预处理返回
message JoinRoomBeforeSm {
	optional int32 code = 1;//1不用切服务器 2需要切
	optional SwServer sw = 2;
	optional string roomId = 3;
}
//加入房间请求
message JoinRoomCm {
	optional string roomId = 1;
	optional JulebuRoom julebuRoom = 2;
	optional int32 seatIndex = 3;
}
//加入房间返回
message JoinRoomSm {
	optional int32 code = 1;//1成功
	optional int32	seatIndex = 2;//座位号
	optional string chatRoomId = 3;//聊天室
	repeated UserInfo users = 4;//玩家列表
	optional PlayType playType = 5;//玩法
	optional string roomId = 6;
	optional JulebuRoom julebuRoom = 7;
}
//房间加入推送
message JoinRoomCast {
	optional int32 delSeatIndex = 1;//退出房间的座位号
	optional UserInfo addUser = 2;//加入房间的
	optional int32 ncont = 3;
}
//准备请求
message PrepareRoomCm{
}
//准备返回
message PrepareRoomSm{
	optional int32 code = 1;//1成功
}
//准备广播
message PrepareRoomCast{
	optional int32 seatIndex = 1;//座位号
	optional int32 ncont = 2;
}
//取消准备请求
message CancelPrepareCm{
}
//取消准备返回
message CancelPrepareSm{
	 optional int32 code = 1;//1ok
}
//取消准备广播
message CancelPrepareCast{
	 optional int32 seetIndex = 1;//位置
}
//离开房间请求
message LeaveRoomCm {
}
//离开房间返回
message LeaveRoomSm {
	optional int32 code = 1;//1成功
}
//解散房间请求
message DissolveRoomCm {
}
//解散房间返回
message DissolveRoomSm {
	optional int32 code = 1;//1成功
}
//解散房间广播
message DissolveRoomCast {
	optional int32 ncont = 1;
}
//玩家解散房间请求
message MemberDissolveRoomCm {
	optional bool agree = 1;//
}
//玩家解散房间返回
message MemberDissolveRoomSm {
	optional int32 code = 1;//1成功
}
//玩家解散房间广播
message MemberDissolveRoomCast {
	optional int32 isOk = 1;//是否解散成功 0等待 1成功 2失败 
	repeated int32 agreeSeetIndex = 2;//同意解散
	repeated int32 disagreeSeetIndex = 3;//拒绝解散
	optional int64 memberDisInMillis = 4;//解散剩余（毫秒）
	optional int32 ncont = 5;
}
//断线广播
message OfflineCast {
	optional int32 seetIndex = 1;//座位号
	optional int32 isOk = 2;//2断线了1上线了
	optional int32 ncont = 3;
}
//语音房间信息保存请求
message ImInfoSaveCm{
	optional int32 index = 1;
}
//语音房间信息保存返回
message ImInfoSaveSm{
	optional int32 code = 1;//1成功
}
//语音房间信息保存广播
message ImInfoSaveCast{
	optional int32 seetIndex = 1;
	optional int32 imSeatIndex = 2;
}
//聊天请求
message ChatCm{
	optional int32 id = 1;
	optional string content = 2;
	optional int32 type = 3;
}
//聊天返回
message ChatSm{
	optional int32 code = 1;//1成功
}
//聊天广播
message ChatCast{
	optional int32 seetIndex = 1;
	optional int32 type= 2; //1表示表情，2表示固定聊天条目，3表示自定义文字
    optional int32 id= 3;  //表情id或者固定聊天id
	optional string content = 4;//自定义聊天消息内容
}
//语音请求
message ImCm{
	optional string fileid = 1;
}
//语音返回
message ImSm{
	optional int32 code = 1;//1成功
}
//语音广播
message ImCast{
	optional  string fileid = 1;
	optional int32 seetIndex = 2;
}
//代开房请求
message ProxyCreateRoomCm{
	optional PlayType playType = 1;//玩法设置
}
//代开房返回
message ProxyCreateRoomSm{
	optional int32 code = 1; //1成功 2失败
	optional string roomId = 2 ;
	optional PlayType playType = 3;//玩法设置
}
message ProxyUserInfo{
	optional int64 uid = 1;//玩家名
	optional string headimg = 2;//头像
	optional string nickname = 3;//玩家昵称
}
message ProxyRoom{
	optional string roomId = 1;//房间ID
	optional PlayType playType = 2;//玩法
	optional int32 state = 3;//状态 1未开始 2已经开始 3结束
	repeated ProxyUserInfo user = 4;//玩家列表
}
//房间列表推送
message ProxyRoomsCast{
	repeated ProxyRoom rooms = 1;
}
//增加房间推送
message ProxyRoomsAddCast{
	repeated ProxyRoom rooms = 1;
}
//解散房间推送
message ProxyRoomsDelCast{
	repeated string roomIds = 1;
}
//房间状态改变推送
message ProxyRoomsUpdateCast{
	repeated ProxyRoom rooms = 1;
}
//代理解散房间请求
message ProxyRoomsDissovleCm{
	optional string roomId = 1;
}
//代理解散房间返回
message ProxyRoomsDissovleSm{
	optional int32 code = 1; //1成功 2失败
}



//观战预处理请求
message ObserveBeforeCm {
	optional string roomId = 1;
}

//观战预处理返回
message ObserveBeforeSm {
	optional int32 code = 1;//1不用切服务器 2需要切
	optional SwServer sw = 2;
	optional string roomId = 3;
}

//观战请求
message ObserveCm {
	optional string roomId = 1;
	optional JulebuRoom julebuRoom = 2;
}

//观战返回
message ObserveSm {
	optional int32 code = 1; //1成功 2失败
	repeated UserInfo users = 2;//玩家列表
	optional int32 seatNum = 3;	//座位总数
	optional PlayType playType = 4;//玩法设置
	optional string roomId = 5;
	optional JulebuRoom julebuRoom = 6;
}

//停止观战请求
message StopObserveCm {
}

//停止观战返回
message StopObserveSm {
	optional int32 code = 1;	//1成功 2失败
}

//创建俱乐部房间请求
message TryCreateJulebuRoomCm {
	optional JulebuRoom julebuRoom = 1;
}

//创建俱乐部房间返回
message TryCreateJulebuRoomSm {
	optional int32 code = 1;	//1成功 2失败
	optional JulebuRoom julebuRoom = 2;
}
